package tudo;

public class switchs {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numero=1;
		switch(numero){
		case 1:
			System.out.println("UM");
			break;
		case 2:
			System.out.println("DOIS");
			break;
		case 3:
			System.out.println("TRES");
			break;
		default :
			System.out.println("NENHUM");
			break;
		}
	}

}
